import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import { getHesaps } from "../services/HesapService";
import HesapBilgileri from "./HesapBilgileri";

function Takas() {
  const [hesaps, setHesaps] = useState([]);
  const [seciliHesap, setSeciliHesap] = useState({});
  const [hedefHesap, setHedefHesap] = useState({});
  const getData = async () => {
    const result = await getHesaps();
    console.log(result);
    setHesaps(result.data);
  };

  useEffect(() => {
    getData();
  }, []);
  function handleSelect(item) {
    setSeciliHesap(item)
    console.log("Hello",item);
  }
  function handlehedefSelect(item) {
    setHedefHesap(item)
    //oranlari getir
    axios.post('http://localhost:8081/api/v1/oran',{paraBirimi:seciliHesap.hesapTur,paraBirimiTakas:hedefHesap.hesapTur}).then(function (response) {
        // console.log(response);
         localStorage.setItem("acces_token",response.data.acces_token);
         localStorage.setItem("refresh_token",response.data.refresh_token);
       })
       .catch(function (error) {
         console.log(error);
       });
    console.log(item);
  }

  return (
    <>
    {seciliHesap.hesapNo!==undefined ?<p className="m-4">Secili Hesap {seciliHesap.hesapNo}</p>:<p className="m-4">Takas yapmak istediğiniz hesabı seçiniz.</p>}
    <Container>
        <Row xs={1} md={2} className="g-4">
                {hesaps.map((item, index) => {                    
                  return (
                    <React.Fragment key={index} >
                      <Col onClick={() => handleSelect(item)}> 
                          <Card  style={{ width: '18rem' }}>
                           
                            <Card.Body >
                              <Card.Title>Hesap No: {item.hesapNo}</Card.Title>
                              <Card.Text>Hesap Turu: {item.hesapTur}</Card.Text>
 
                            </Card.Body>
                                    Bakiye: {item.bakiye}
                          </Card>
                   
                      </Col>
                    </React.Fragment>
                         )
                        })}
        </Row>
       
        {seciliHesap.hesapNo!==undefined&&
 <>
        {hedefHesap.hesapNo!==undefined ?<p className="m-4">Secili Hesap {hedefHesap.hesapNo}</p>:<p className="m-4">Takas yapmak istediğiniz hesabı seçiniz.</p>}
    
        <Row xs={1} md={2} className="g-4 mt-4">
                {hesaps.map((item, index) => { 
                      if (item===seciliHesap) {
                       return; 
                       }                   
                  return (
                  
                    <React.Fragment key={index} >
                      <Col onClick={() => handlehedefSelect(item)}> 
                          <Card  style={{ width: '18rem' }}>
                           
                            <Card.Body >
                              <Card.Title>Hesap No: {item.hesapNo}</Card.Title>
                              <Card.Text>Hesap Turu: {item.hesapTur}</Card.Text>
 
                            </Card.Body>
                                    Bakiye: {item.bakiye}
                          </Card>
                   
                      </Col>
                    </React.Fragment>
                         )
                        })}
        </Row>
        </>

                    }

    </Container>  
                  
    </>
  );
}

export default Takas;
